"use strict";let film={name:"Lord Of The Ring",director:"Piter Jackson",year:2001,oscar:!0,rate:5},elem=document.getElementById("editFilm"),elem2=document.getElementById("yourFilm");function editFavoriteFilm(){elem.style.display="block",elem2.style.display="none"}function saveFavoriteFilm(){let t=document.formFilm;film.name=t.name.value,film.director=t.director.value,film.year=t.year.value,film.rate=t.rate.value;let e=t.oscarYes.checked;film.oscar=!!e}function saveAndShow(){return saveFavoriteFilm(),showFavoriteFilm()}function showFavoriteFilm(){return elem.style.display="none",elem2.style.display="block",`<table class="table table-hover">\n  <thead>\n    <tr>\n      <th scope="col">#</th>\n      <th scope="col">Options</th>\n      <th scope="col">Film</th>\n    </tr>\n  </thead>\n  <tbody>\n    <tr>\n      <th scope="row">1</th>\n      <td>Your favorite film is</td>\n      <td class="font-weight-bold">${film.name}</td>\n    </tr>\n    <tr>\n      <th scope="row">2</th>\n      <td>Director</td>\n      <td class="font-weight-bold">${film.director}</td>\n    </tr>\n    <tr>\n      <th scope="row">3</th>\n      <td>Year of the film</td>\n      <td class="font-weight-bold">${film.year}</td>\n    </tr>\n    <tr>\n      <th scope="row">4</th>\n      <td>Oscars</td>\n      <td class="font-weight-bold">${film.oscar}</td>\n    </tr>\n    <tr>\n      <th scope="row">5</th>\n      <td>Rate of film max 5</td>\n      <td class="font-weight-bold">${film.rate}</td>\n    </tr>\n  </tbody>\n</table>`}