"use strict";let btnCard=document.querySelector("#btnCard"),btnClear=document.querySelector("#btnClear"),ul=document.querySelector("#ul-card");function Card(e,t,n){this.name=e,this.surname=t,this.type=n,this.date=generateDate(),this.cvv=generateCvv()}function generateDate(){let e=(new Date).getFullYear()+Math.floor(6*Math.random()),t=Math.floor(12*Math.random())+1;t<10&&(t="0"+t);let n=`${t}/${e}`;return console.log(n),n}function random(){let e=Math.random();return e<.33?"VISA":e<.66?"MASTERCARD":"MAESTRO"}function generateCvv(){let e=Math.random().toString();return e=e.slice(2,5)}function createCard(e){let t=document.createElement("li");t.innerText=`Name: ${e.name}, Surname: ${e.surname}, Type: ${e.type}, End: ${e.date}, CVV: ${e.cvv}`,ul.append(t)}btnCard.addEventListener("click",()=>{console.log("btnCard");let e=document.forms.data.name.value;e||(e="James");let t=document.forms.data.surname.value;t||(t="Bond");let n=document.forms.data.type.value;"RANDOM"===n&&(n=random(),console.log("random")),createCard(new Card(e,t,n))}),btnClear.addEventListener("click",()=>{ul.innerText=""});